mapper.insert:
REPLACE INTO
bi_lac(
lac,
city
)
VALUES

	(
	#{item.lac},
	#{item.city}
	)

mapper.select:
select ${columnName}
name,count(id) value from bi_visitor

	where DATE_FORMAT(time,'%Y-%m-%d') >= #{startTime} and
	DATE_FORMAT(time,'%Y-%m-%d') <= #{endTime}

group by ${columnName} order by
count(id) desc limit 10

select age name,count(age) value
from ( select age from bi_visitor

	where DATE_FORMAT(time,'%Y-%m-%d') >= #{startTime} and
	DATE_FORMAT(time,'%Y-%m-%d') <= #{endTime}

) a group by age
desc limit 10

select ${columnName} name,count(${columnName}) value from
(select
${columnName},time,ipv4 from
bi_visitor where gender!='不详'

	and time >= #{startTime} and
	time <= #{endTime}

) a join (select userId,time from ${tableName} where shopId=#{shopId})
b on a.ipv4=b.userId and a.time=b.time
group by
${columnName}

select IFNULL(z.city,'省外') name,count(${columnName}) value from (select ${columnName},time,ipv4 from bi_visitor
where time >= #{startTime} and time <= #{endTime}
) a join (select userId,time from ${tableName} where shopId=#{shopId}) b on a.ipv4=b.userId and a.time=b.time left join bi_lac z on z.lac=${columnName} group by z.city

select IFNULL(z.city,if(x.city is null,'无法统计','不详')) name,count(${columnName}) value from (select localAddress, ${columnName},time,ipv4 from bi_visitor
where time >= #{startTime} and time <= #{endTime}
) a join (select userId,time from ${tableName} where shopId=#{shopId}) b on a.ipv4=b.userId and a.time=b.time left join bi_lac z on z.lac=${columnName} left join bi_lac x on x.lac=a.localAddress group by name

select age name,count(age) value from ( select age from ( select a.age from (select ipv4,time,age from bi_visitor
where time >= #{startTime} and time <= #{endTime}
) a join (select userId,time from ${tableName} where shopId=#{shopId}) b on a.ipv4=b.userId and a.time=b.time ) f ) g group by age desc

select expendAbility name,count(expendAbility) value from ( select a.expendAbility from (select ipv4,time,expendAbility from bi_visitor
where time >= #{startTime} and time <= #{endTime}
) a join (select userId,time from ${tableName} where shopId=#{shopId}) b on a.ipv4=b.userId and a.time=b.time ) f group by expendAbility desc

