and time >= #{startTime} and time <= #{endTime}
) a join (select userId,time from ${tableName} c join bi_shop d on c.shopId=d.id join bi_maps e on d.mapId=e.mapId join bi_store f on e.storeId=f.id join bi_shop_category g on g.id=d.categoryId where e.storeId=#{storeId} and d.categoryId=#{categoryId} ) b on a.ipv4=b.userId and a.time=b.time group by ${columnName}

